const validator = require('validator')
const mongoose = require('mongoose');
const bcrypt = require('bcrypt');
const defaultProfile = "data:image/png;base64,"

const UserSchema = new mongoose.Schema({
    user_type: {
        type: String,
        required: true,
    },
    branch_id_assigned: {
        type: mongoose.Schema.Types.ObjectId,
        ref: 'branchs',
    },
    name: {
        type: String,
        required: true,
    },
    user_name: {
        type: String,
        required: true,
        unique: true,
    },
    email: {
        type: String,
        required: true,
        unique: true,
        lowercase: true, 
        validate: [validator.isEmail, "Please Enter Valid Email"],
        
    },
    password: {
        type: String,
        required: true,
    },
    userImage: {
        type: String,
        default: defaultProfile,
    },
    added_by: {
        type: mongoose.Schema.Types.ObjectId,
        ref: 'users',
      },
    isMainAdmin: {
        type: Boolean,
        default: false,
    },
}, { timestamps: true });

UserSchema.statics.login = async function (user_name, password) {
    if (!user_name || !password) {
        throw Error('All fields must be filled');
    }

    const user = await this.findOne({ user_name });

    if (!user) {
        throw Error('Incorrect User');
    }

    const match = await bcrypt.compare(password, user.password);

    if (!match) {
        throw Error('Incorrect password!!!');
    }

    return user;
};

UserSchema.statics.loginByEmail = async function (email) {
    if (!email) {
        throw Error('Email must be provided');
    }

    const user = await this.findOne({ email });

    if (!user) {
        throw Error('User not found with this email');
    }

    return user;
};

const UserModel = mongoose.model('users', UserSchema);
module.exports = UserModel;
